import React from "react";
import {store} from "./store";
window.store = store;

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      count : store.getStore().reducer.count
    }
  }
  render(){
    return (
      <div>
        {this.state.count}
        <buton onClick = {function(){
          store.dispatch({type:"INCREMENT"});
          this.setState({count:store.getStore().reducer.count});
        }}>+</buton>
      </div>
    );
  }
}

export default App;
